/**
 * 全站路由配置
 *
 * meta参数说明
 * keepAlive是否缓冲页面
 * isTab是否加入到tag导航
 * isAuth是否需要授权
 */
import VueRouter from 'vue-router';
import SelfPageRouter from '@/router/page/'
import SelfViewsRouter from '@/router/views/'
import AvueRouter from './avue-router';
import Vue from 'vue';
import store from '../store';
import i18n from '@/lang' // Internationalization
import { isEmpty } from '../utils/util'
let Router = new VueRouter({
    scrollBehavior(to, from, savedPosition) {
        if (savedPosition) {
            return savedPosition
        } else {
            if (from.meta.keepAlive) {
                from.meta.savedPosition = document.body.scrollTop;
            }
            return {
                x: 0,
                y: to.meta.savedPosition || 0
            }
        }
    },
    routes: [],
    mode:"hash"
});
AvueRouter.install(Vue, Router, store, i18n);
Router.$avueRouter.formatRoutes(store.state.user.menu, true);
if(!isEmpty(SelfPageRouter)&&!isEmpty(SelfViewsRouter)){
    Router.addRoutes([...SelfPageRouter,...SelfViewsRouter]);
}else if(!isEmpty(SelfPageRouter)){
    Router.addRoutes([...SelfPageRouter]);
}else if(!isEmpty(SelfViewsRouter)){
    Router.addRoutes([...SelfViewsRouter]);
}
Vue.use(VueRouter);
export default Router;