<template>
    <el-dialog class="selDwDg" :width="dialogWidth" v-if="dialogTysqVisible" append-to-body top="5vh"   :center="dialogOp.center" title="选择单位"  :visible.sync="dialogTysqVisible"  @close="distroy">
        <ContentMain :footerHeight="height">
            <template>
                    <el-input placeholder="输入关键字进行过滤" v-model="filterText"> </el-input>
                    <el-tree ref="tree"
                        v-loading="loading"
                        class="filter-tree"
                        :data = "treeData"
                        :props="treeProps"
                        :filter-node-method="filterNode"
                        v-if="dialogTysqVisible"
                        show-checkbox
                        accordion></el-tree>
            </template>
            <template slot="footer">
                <div style="text-align:center;padding: 10px 0">
                    <el-button type="primary" @click="doSave">保 存</el-button>
                    <el-button  @click="distroy">取 消</el-button>
                </div>
            </template>
        </ContentMain>
    </el-dialog>
</template>

<script>
import {mapGetters} from "vuex";
import {deepClone,jsonTree,mergeArray,isEmpty} from "npm-pansoft-base/lib/utils/util";

export default {
    name:"SelTysqAnyDwYhzx",
    data(){
      return {
        height:'60px',
        filterText: '',
        dialogTysqVisible: this.tysqDwShow,
        dialogOp:{
            center : false
        },
        myDwTreeParams:{
            identity:this.dwTreeParams.identity
        },
        treeData:[],
        treeProps: {
          children: 'children',
          label: 'text',
          isLeaf: 'leaf'
        }
      }
    },
    created() {
       
    },
    mounted(){
        this.getList();
    },
    props: {
        tysqDwShow: {
            type: Boolean,
            default: false
        },dataOptions:{
            type: Object,
            default:() =>{}
        },dwTreeParams:{
            type:Object,
            default:()=>{
                return {
                    identity:"1"
                } 
            }
        },dialogWidth:{
            type:String,
            default:"500px"
        },
        onOk:{
            type:Function,
            default:()=>{}
        },
        isLoading:{
            type: Boolean,
            default: true
        }
    },
    watch: {
        tysqDwShow () {
            //此处是关键
            this.dialogTysqVisible = this.tysqDwShow;
        },
        filterText(val) {
            this.$refs.tree.filter(val);
        }
    },
    computed:{
        loading(){
          if(isEmpty(this.treeData)&&this.isLoading){
            return true;
          }else{
            return false;
          }
        }
    },
    methods: {
        getList(){
            var that = this;
            this.axios.apiGet({
                url: "/databank/getFullTree",
                data:this.myDwTreeParams,
                call:function(res){
                    // console.log(res);
                    that.treeData = res.data.result;
                }
            });
        },
        getDwtree(resolve){
            var that=this;
            this.axios.apiPost({
                url:'/databank/getFullTree',
                data:this.myDwTreeParams,
                call:function(res){
                    resolve(res.data.result);
                }
            });
        },
        canel(){
            this.dialogTysqVisible=false;
            //重置数据无效 this.$refs.avueForm.resetForm();
        },
        distroy(){
            this.$emit('update:tysqDwShow', false);
        }, 
        filterNode(value, data) {
            if (!value) return true;
            return data[this.treeProps.label].indexOf(value) !== -1;
        },
        doSave(){
            this.treeData=this.$refs.tree.getCheckedNodes();
            if(this.treeData.length==0){
                this.$message.error("请选择单位！");
            }else{
                this.dataOptions.retData=deepClone(this.treeData);
                this.onOk(deepClone(this.treeData));
                //this.$emit("sendData", this.rightRyData);
                this.distroy();
            }
        },
        loadNode(node, resolve){
            if (node.level === 0) {
                this.getDwtree(resolve);
            }else{
                this.myDwTreeParams.id=node.data.id;
                this.getDwtree(resolve);
            }
        }
    }
}
</script>

<style scoped>
  .el_input_search {
    width: 120px !important;
  }
  .el-header{
      padding:0 5px
  }
  .selDwDg>>>.mainDg{
      height: 400px;
  }
</style>