<template>
    <el-dialog v-if="dialogTysqVisible" append-to-body :width="dialogWidth" top="5vh"   :center="dialogOp.center" title="选择人员(多选)"  :visible.sync="dialogTysqVisible"  @close="distroy">
        <!--父组件传过来的formData 赋值给新值newFormData 如果不赋值控制台会报错 经过努力 avue的方式传值有问题 js会报错 其实没有错 官方插件bug-->
            <el-container style="height: 500px; border: 1px solid #eee">
                <el-aside width="300px" style="background-color: rgb(238, 241, 246)">
                        <el-input placeholder="输入关键字进行过滤" v-model="filterText"> </el-input>
                        <el-tree ref="tree"  
                        class="filter-tree"
                        :props="treeProps"
                        @node-click="handleNodeClick" 
                        :load="loadNode"
                        lazy
                        :filter-node-method="filterNode"
                        :expand-on-click-node="true"
                        v-if="dialogTysqVisible"
                        accordion></el-tree>
                </el-aside>
                <el-container>
                     <el-header style="height: auto; font-size: 12px">
                        <div style="text-align: left;float:left">
                            <el-tag type="success"> 可选人员</el-tag>
                        </div>
                        <div style="text-align: right;" class="seach_div">
                            <el-input class="el_input_search" @keyup.enter.native="gteRyByXm" v-model="inputParams.dlid" size="mini" placeholder="请输入登陆id"/>
                            <el-input class="el_input_search" @keyup.enter.native="gteRyByXm" v-model="inputParams.yhxm"  size="mini" placeholder="请输入用户姓名"/>
                            <el-tooltip class="item" effect="dark" content="搜索" placement="top">
                                <el-button type="default" icon="el-icon-search" circle size="mini" @click="gteRyByXm()"></el-button>
                            </el-tooltip>
                            <el-tooltip class="item" effect="dark" content="清除" placement="top">
                                <el-button type="default" icon="el-icon-refresh-right" circle size="mini" @click="clear()"></el-button>
                            </el-tooltip>
                            <el-tooltip class="item" effect="dark" content="全部选择" placement="top">
                                <el-button type="default" icon="el-icon-d-arrow-right" circle size="mini" @click="choseAll()"></el-button>
                            </el-tooltip>
                        </div>
                    </el-header >
                    <el-main>
                         <el-table :data="leftRyData" style="width: 100%" @row-dblclick="selRy" height="400"> 
                             <el-table-column
                                label="序号"
                                type="index"
                                width="50">
                            </el-table-column>
                             <el-table-column
                                prop="yhxm"
                                label="姓名"
                                width="180">
                            </el-table-column>
                            <el-table-column
                                prop="dwmc"
                                label="单位名称"
                                width="160">
                            </el-table-column>
                        </el-table>
                    </el-main>
                </el-container>
                 <el-container>
                    <el-header style="height: auto;text-align: right; font-size: 12px">
                         <div style="text-align: left;float:left">
                            <el-tag> 已选人员</el-tag>
                        </div>
                        <div style="text-align: right;">
                           <el-button type="default" icon="el-icon-d-arrow-left" circle size="mini" @click="clearAll()"></el-button>
                        </div>
                    </el-header >
                    <el-main>
                        <el-table :row-class-name="tableRowClassName" :data="rightRyData" style="width: 100%" @row-dblclick="unSelRy" height="420"> 
                            <el-table-column
                                label="序号"
                                type="index"
                                width="50">
                            </el-table-column>
                            <el-table-column
                                prop="yhxm"
                                label="姓名"
                                width="180">
                            </el-table-column>
                            <el-table-column
                                prop="dwmc"
                                label="单位名称"
                                width="160">
                            </el-table-column>
                        </el-table>
                    </el-main>
                </el-container>
            </el-container>
            <el-footer>
                 <div style="text-align:center;padding: 10px 0">
                    <el-button type="primary" @click="doSave">保 存</el-button>
                    <el-button  @click="distroy">取 消</el-button>
                </div>
            </el-footer>
    </el-dialog>
</template>

<script>
import {mapGetters} from "vuex";
import {deepClone,jsonTree,mergeArray,isEmpty} from "npm-pansoft-base/lib/utils/util";

export default {
    name:"SelTysqAnyRy",
    data() {
      return {
        filterText: '',
        dialogTysqVisible: this.tysqShow,
        dialogOp:{
            center : false
        },
        inputParams:{
            yhxm:"",
            dlid:""
        },
        myDwTreeParams:{
            dwbm:this.dwTreeParams.dwbm,
            maxdwjb:this.dwTreeParams.maxdwjb,
            mindwjb:this.dwTreeParams.mindwjb,
            order:this.dwTreeParams.order,
            dwid:this.dwTreeParams.dwid,
        },
        leftRyData:[],
        rightRyData:[],
        treeData:[],
        treeProps: {
          children: 'children',
          label: 'label',
          isLeaf: 'leaf'
        }
      }
    },
    created() {
    },
    mounted(){
        if(this.dwTreeParams.tysqversion=="1.0"){
            this.myDwTreeParams.dwbm=this.dwTreeParams.dwbm;
            this.myDwTreeParams.order="DWPWSX";
            
        }else{
            this.myDwTreeParams.dwid=this.dwTreeParams.dwid;
            this.myDwTreeParams.order="DWBM";
        }
    },
    props: {
        tysqShow: {
            type: Boolean,
            default: false
        },dataOptions:{
            type: Object,
            default:() =>{}
        },dwTreeParams:{
            type:Object,
            default:()=>{
                return {
                    dwbm:"CNPC.PetroChina.TLM",
                    maxdwjb:"100",
                    mindwjb:"0",
                    order:"DWPWSX",
                    dwid:"",
                    tysqversion:"1.0"
                } 
            }
        },
        onOk:{
            type:Function,
            default:()=>{}
        },
        dialogWidth:{
            type:String,
            default:"1300px"
        }
    },
    watch: {
        tysqShow () {
            //此处是关键
            this.dialogTysqVisible = this.tysqShow;
        },
        filterText(val) {
            this.$refs.tree.filter(val);
        }
    },
    computed:{
    },
    methods: {
        getDwtree(resolve){
            var that=this;
            this.axios.apiPost({
                url:'/tysq/queryDwTreeByNoSync',
                data:this.myDwTreeParams,
                call:function(res){
                    let tree=jsonTree(res.data,{
                            id: 'id',
                            pid: 'pid',
                            children: 'children'
                    });
                    // console.log(tree);
                    tree.forEach(item=>{
                        if(item.state=="open"){
                            item.leaf=true;
                        }
                    })
                    resolve(tree);
                }
            });
        },
        getRyByDw(){
           this.getRyData();
        },
        gteRyByXm(){
            this.getRyData();
        },
        clear(){
            this.inputParams.yhxm="";
            this.inputParams.dlid="";
            if(this.dwTreeParams.tysqversion=="1.0"){
                this.inputParams.dwbm="";
            }else{
                this.inputParams.dwid="";
            }
            this.getRyData();
        },
        canel(){
            this.dialogTysqVisible=false;
            //重置数据无效 this.$refs.avueForm.resetForm();
        },
        distroy(){
            this.$emit('update:tysqShow', false);
            //关闭的时候重置
            this.rightRyData = [];
            this.leftRyData = [];
        }, 
        filterNode(value, data) {
            if (!value) return true;
              return data[this.treeProps.label].indexOf(value) !== -1;
        },
        handleNodeClick(data){
            if(this.dwTreeParams.tysqversion=="1.0"){
                this.inputParams.dwbm=data.dwbm;
            }else{
                this.inputParams.dwid=data.dwid;
            }
            this.getRyByDw();
        },
        choseAll(){
            this.rightRyData=mergeArray(deepClone(this.rightRyData),deepClone(this.leftRyData),"yhid");
        },
        clearAll(){
            this.rightRyData=[];
        },
        selRy(row, event){
            let rows=this.rightRyData;
            for(var x in rows){
                if(rows[x].yhid == row.yhid){
                    this.$message.error("请勿重复添加！");
                    return;
                }
            }
            this.rightRyData.push(row);
        },
        unSelRy(row, event){
            this.rightRyData.splice(row.index,1);
        },
        doSave(){
            if(this.rightRyData.length==0){
                this.$message.error("请选择人员！");
            }else{
                this.dataOptions.retData=deepClone(this.rightRyData);
                this.onOk(deepClone(this.rightRyData));
                //this.$emit("sendData", this.rightRyData);
                this.distroy();
            }
        },
        tableRowClassName({row, rowIndex}) {
            row.index=rowIndex;
        },
        getRyData(){
            var that=this;
            this.axios.apiPost({
              url:'/tysq/queryYhByDw',
              data:this.inputParams,
              call:function(res){
                  that.leftRyData=res.data.rows;
              }
            });
        },
        loadNode(node, resolve){
            if (node.level === 0) {
                this.getDwtree(resolve);
            }else{
                this.myDwTreeParams.id=node.data.id;
                this.getDwtree(resolve);
            }
        }
    }
}
  //显示组件不要用avue 来封装
</script>
<style scoped>
  .seach_div>>>.el_input_search {
    width: 120px !important;
  }
  .el-header{
      padding:0 5px
  }
</style>