<template>
<el-dialog class="time-line" :width="width" :height="height" v-if="dialogVisible" append-to-body  top="5vh"   :title="title"  :visible.sync="dialogVisible"  @close="distroy">
    <div class="block">
        <div class="radio">
            排序：
            <el-radio-group v-model="reverse">
                <el-radio :label="false">正序</el-radio>
                <el-radio :label="true">倒序</el-radio>
            </el-radio-group>
        </div>
        <p></p>
        <el-timeline  v-if="dialogVisible" :reverse="reverse">
            <el-timeline-item
            v-for="(activity, index) in activities"
            :key="index"
            :icon="activity.icon"
            :type="activity.type"
            :color="activity.color"
            :size="activity.size"
            :timestamp="activity.timestamp">
            <!-- {{activity.content}} -->
                <div v-html="activity.content"></div>
            </el-timeline-item>
        </el-timeline>
    </div>
</el-dialog>
</template>

<script>
export default {
    name:"TimeLine",
    data(){
        return{
            dialogVisible:this.dialogShow,
            reverse:false
        }
    },
    props:{
        activities:{
            type:Array,
            default:()=>{}
        },
        title:{
            type:String,
            default:"时光线"
        },
        width:{
            type:String,
            default:"520px"
        },
        height:{
            type:String,
            default:"400px"
        },
        dialogShow:{
            type:Boolean,
            default:false,
        },
        onClose:{
            type:Function,
            default:()=>{}
        }
    },
    methods:{
        distroy(){
            this.onClose();
            this.$emit('update:dialogShow', false);
        }
    }
}
</script>

